import './App.css'
import Navigation from './components/navigation/Navigation.jsx';
import Footer from './components/footer/Footer.jsx';
import AllWordsButton from './components/allwordsbutton/AllWordsButton';
import LoginForm from './components/navigation/loginform/LoginForm.jsx';
import RegisterForm from './components/navigation/registerform/RegisterForm.jsx';
import Main from './components/main/Main.jsx';
import NewWord from './components/newword/NewWord.jsx';
import Missing from './components/missingpage/Missing.jsx';
import { BrowserRouter as Router, Routes, Route } from'react-router-dom';



function App(){
  const onAddWord = (word) => {
    // Отправка нового слова на сервер
    fetch('https://itgirlschool.justmakeit.ru/api/words', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(word),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Ошибка сети');
      }
      return response.json();
    })
    .then(data => {
      console.log('Слово успешно добавлено:', data);
      // Здесь вы можете выполнить какие-то действия после успешного добавления слова
    })
    .catch(error => {
      console.error('Произошла ошибка:', error);
      // Здесь вы можете обработать ошибку, например, показать сообщение пользователю
    });
  };


  return (
    <Router>
      <div>
      <Navigation />
        <Routes>
          <Route path="/" element={ <AllWordsButton /> } />
          <Route path="/main" element={ <Main /> } />
          <Route path="/newword" element={<NewWord onAddWord={onAddWord} />} />
          <Route path="/registerform" element={ <RegisterForm /> } />
          <Route path="/loginform" element={ <LoginForm /> } />
          <Route path="*" element={ <Missing/> } />
        </Routes>
        <Footer /> 
      </div>
    </Router>
  );
};

export default App;
