import React, { useState } from 'react';
import styles from './NewWord.module.css';

const NewWord = ({ onAddWord }) => {
  const [newWord, setNewWord] = useState({
    english: '',
    transcription: '',
    russian: '',
    tag: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewWord(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Проверяем, что все поля заполнены
    if (newWord.english && newWord.transcription && newWord.russian && newWord.tag) {
      // Если все поля заполнены, вызываем обработчик onAddWord
      onAddWord(newWord);

      // Сбрасываем состояние формы
      setNewWord({
        english: '',
        transcription: '',
        russian: '',
        tags: ''
      });
    } else {
      // Если какое-то поле не заполнено, вы можете показать сообщение об ошибке или сделать что-то еще
      console.log('Пожалуйста, заполните все поля');
    }
  };

  return (
    <div className={styles.NewWord}>
      <h2>Добавить новое слово</h2>
      <form>
        <input
          type="text"
          name="english"
          value={newWord.english}
          onChange={handleChange}
          placeholder="слово на англ.яз"
        />
        <input
          type="text"
          name="transcription"
          value={newWord.transcription}
          onChange={handleChange}
          placeholder="транскрипция"
        />
        <input
          type="text"
          name="russian"
          value={newWord.russian}
          onChange={handleChange}
          placeholder="перевод"
        />
        <input
          type="text"
          name="tag"
          value={newWord.tag}
          onChange={handleChange}
          placeholder="тег"
        />
        <button type="submit" className={styles.button} onClick={handleSubmit}>Добавить</button>
      </form>
    </div>
  );
};

export default NewWord;
